function install(hook, vm) {
  let userOptions = vm.config.footer;

  let Link_style = 'style="display:inline-block;text-decoration:none;height:20px;line-height:20px;"';
  let P_style = 'style="float:left;height:20px;line-height:20px;margin: 0px 0px 0px 5px; color:#939393;"'
  let GA_Info = "川公网安备 51012202001091号";
  let GA_Link = "http://www.beian.gov.cn/portal/registerSystemInfo?recordcode=51012202001091"
  let ICP_Info = "蜀ICP备2021027136号-1";
  let ICP_Link = "https://beian.miit.gov.cn/";
  let Record = [
    '<div style="width:600px;margin:0 auto; padding:20px 0;">',
    `<a target="_blank" href="${GA_Link}" ${Link_style}><p ${P_style}>${GA_Info}</p></a>`,
    '&nbsp;&nbsp;',
    `<a target="_blank" href="${ICP_Link}" ${Link_style}><p ${P_style}>${ICP_Info}</p></a>`,
    '</div>',
  ].join('');
  let copy_prefix = userOptions && userOptions.prefix
    ? userOptions.prefix
    : ''

  let copy = userOptions && userOptions.copy ? userOptions.copy : Record;
  let suffix = userOptions && userOptions.suf ? userOptions.suf : '';
  let auth =
    userOptions && userOptions.auth
      ? userOptions.auth
      : '</p></a>';
  let style = userOptions && userOptions.style ? `style="${userOptions.style}"` : 'align:center;height:20px;line-height:20px;margin: 0 auto; color:#939393;';
  let clazz = userOptions && userOptions.class ? `class="${userOptions.class}"` : '';
  let pre = userOptions && userOptions.pre ? `${userOptions.pre}`
    : '<hr/>';

  var footer = `${pre}<footer ${style} ${clazz}>${copy_prefix} ${copy} ${auth}&nbsp;&nbsp; ${suffix}</footer>`;

  hook.afterEach(function (html) {
    return html + footer;
  });
}
window.$docsify = window.$docsify || {};
window.$docsify.plugins = (window.$docsify.plugins || []).concat(install);